mapper.select:
select a.ifcgSeq, a.ifcgName, a.ifcgUseNy, a.ifcgOrder, a.ifcgDesc, a.ifcgDelNy, a.ifcgRegist, a.ifcgEdit, (SELECT count(ifcdSeq) FROM code AS b WHERE 1 = 1 AND b.codegroup_ifcgSeq = a.ifcgSeq) AS xifcdSeqCount FROM yuz.codegroup a WHERE 1 = 1
AND ifcgDelNy = #{shDelNy}
AND ifcgUseNy = #{shUseNy}
AND a.ifcgRegist BETWEEN #{shDateStart} AND #{shDateEnd} AND a.ifcgEdit BETWEEN #{shDateStart} AND #{shDateEnd}
AND ifcgSeq = #{shValue} AND ifcgName LIKE CONCAT('%', #{shValue},'%')
ORDER BY a.ifcgSeq desc

mapper.delete:
DELETE FROM yuz.codegroup
WHERE 1=1
	AND ifcgSeq = #{ifcgSeq}
