mapper.select:
SELECT b.ifcdSeq, b.ifcdName, b.ifcdUseNy, b.ifcdOrder, b.ifcdDesc, b.ifcdDelNy, b.ifcdRegist, b.ifcdEdit, b.codegroup_ifcgSeq, a.ifcgName, (SELECT COUNT(ifcdSeq) FROM yuz.code AS b WHERE b.codegroup_ifcgSeq = b.codegroup_ifcgSeq) AS xifcdSeqCount FROM yuz.code AS b JOIN codegroup AS a ON a.ifcgSeq = b.codegroup_ifcgSeq WHERE 1 = 1 AND ifcdDelNy = #{shDelNy} AND ifcdUseNy = #{shUseNy}
AND ifcgSeq = #{shValue} AND ifcgName LIKE CONCAT('%',#{shValue},'%')
AND b.ifcdRegist BETWEEN #{shDateStart} AND #{shDateEnd} AND b.ifcdEdit BETWEEN #{shDateStart} AND #{shDateEnd}
ORDER BY b.ifcdSeq desc

mapper.delete:
DELETE FROM yuz.code
WHERE 1 = 1
	AND ifcdSeq = #{ifcdSeq}
