mapper.sql:
FROM 
	yuz.product a
WHERE 1 = 1

	AND ifpdDelNy = #{shDelNy}

	AND ifpdUseNy = #{shUseNy}


		AND a.ifpdRegist BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.ifpdEdit BETWEEN #{shDateStart} AND #{shDateEnd}




		AND ifpdSeq = #{shValue}
		AND ifpdName LIKE CONCAT('%', #{shValue},'%')

mapper.insert:
INSERT INTO product( ifpdName, ifpdUseNy, ifpdDesc, ifpdDelNy, ifpdReg, ifpdUpdate, member_ifmmSeq )VALUES( #{ifpdName}, #{ifpdUseNy}, #{ifpdDesc}, #{ifpdDelNy}, now(), now(), #{member_ifmmSeq}
)
SELECT last_insert_id()

mapper.delete:
DELETE FROM yuz.product
WHERE 1=1
	AND ifpdSeq = #{ifpdSeq}
